// SPDX-License-Identifier: MIT
pragma solidity ^0.5.16;

contract DeviceRegistry {
    struct Device {
        address owner;
        bytes publicKey;
        bool registered;
    }

    mapping(string => Device) public devices;

    event DeviceRegistered(string deviceId, address owner, bytes publicKey);
    event DeviceAuthenticated(string deviceId, address owner);

    modifier onlyOwner(string memory deviceId) {
        require(devices[deviceId].owner == msg.sender, "Not the device owner");
        _;
    }

    function registerDevice(string calldata deviceId, bytes calldata publicKey) external {
        require(!devices[deviceId].registered, "Device already registered");

        devices[deviceId] = Device({
            owner: msg.sender,
            publicKey: publicKey,
            registered: true
        });

        emit DeviceRegistered(deviceId, msg.sender, publicKey);
    }

    function authenticateDevice(string calldata deviceId, bytes calldata challenge) external onlyOwner(deviceId) {
        // TODO: Implement PUF verification logic here

        // For simplicity, we'll emit an event indicating successful authentication
        emit DeviceAuthenticated(deviceId, msg.sender);
    }
}
